import json
import sys

def contains_true_or_false(input_string):
    if 'true' in input_string.lower():
        return True
    elif 'false' in input_string.lower():
        return False
    else:
        # Handle the case where neither 'true' nor 'false' is found
        # You can return a default value or raise an exception
        raise ValueError("The input string does not contain 'true' or 'false'.")

i_messages = sys.argv[1]
response_body_json = sys.argv[2]
append_messages = sys.argv[3]

# Export the message from the response
resp = json.loads(response_body_json)
o_choices = json.dumps(resp.get('choices'))
resp = resp.get('choices')[0].get('message')

# Export out the mesage content
if resp['content']:
    o_message_content = resp['content']
else:
    o_message_content = ''

# If there is a tool_call in the message, export that too
if 'tool_calls' in resp and resp['tool_calls']:
    o_tool_calls = json.dumps(resp['tool_calls'])
else:
    o_tool_calls = json.dumps('[]')

# Now export the message to be used for chat history
# OpenAI doesn't like you sending null messages array, must be empty string
for key in ["tool_calls", "function_call"]:
    if resp.get(key) is None:
        resp.pop(key, None)

for key, value in resp.items():
    if value is None:
        resp[key] = ''

if contains_true_or_false(append_messages):
    o_messages = json.loads(i_messages)
    o_messages.append(resp)
else:
    o_messages = resp  # Assign the dictionary directly if not appending

o_messages = json.dumps(o_messages)  # Convert the final result to a JSON string
